const market = require('market');

function assembler(){
	for (var base in window.coins) {
		for (var i = 0; i < window.coins[base].length; i++) {
			var quote = window.coins[base][i];
			var pair = quote+"-"+base;
			window.Pairs[pair] = new market(quote, base);
		}
	}
	console.log("Pairs have been assembled.")
}

module.exports = assembler;