const market = require('market');

function assembler(){
	for (var i in window.pairs) {
		var quote = window.pairs[i].split("-")[0];
		var base = window.pairs[i].split("-")[1];
		window.marketPairs[window.pairs[i]] = new market(quote, base);
		console.log(window.pairs[i]);
		
	}
	console.log("A total of [" + window.pairs.length + "] market pairs assembled.")
}

module.exports = assembler;
